import React from 'react';
import galleryImages from './galleryImages'
import Masonry,{ResponsiveMasonary} from 'react-responsive-masonry';

const MasonryImagesGallery = () => {
  return (
    <div>
      <ResponsiveMasonary>

      </ResponsiveMasonary>
        <Masonry gutter='1rem'>

        {galleryImages.map((item, index) => (
          <div >
            <img src={item} alt={item.alt} key={index} style={{ width:'100%',display:'block',borderRadius:'10px'}}/>
          </div>
        ))}
        </Masonry>
    </div>

  )
}

export default MasonryImagesGallery
